import React, { useState, useEffect } from 'react';

const Student = () => {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [isRegistered, setIsRegistered] = useState(false);
  const [emailVerified, setEmailVerified] = useState(false);
  const [studentProfile, setStudentProfile] = useState({
    name: '',
    email: '',
    phone: '',
    address: '',
    education: ''
  });
  const [applications, setApplications] = useState([]);
  const [admissionsResults, setAdmissionsResults] = useState([]);
  const [documents, setDocuments] = useState({
    transcripts: [],
    certificates: []
  });
  const [jobPostings, setJobPostings] = useState([]);
  const [appliedJobs, setAppliedJobs] = useState([]);
  const [notifications, setNotifications] = useState([]);

  const [loginData, setLoginData] = useState({ email: '', password: '' });
  const [registerData, setRegisterData] = useState({
    name: '',
    email: '',
    password: '',
    confirmPassword: ''
  });

  const [courseApplication, setCourseApplication] = useState({
    institution: '',
    course1: '',
    course2: ''
  });

  const availableInstitutions = [
    {
      id: 1,
      name: 'University of Technology',
      courses: ['Computer Science', 'Electrical Engineering', 'Mechanical Engineering']
    },
    {
      id: 2,
      name: 'Science University',
      courses: ['Biology', 'Chemistry', 'Physics', 'Mathematics']
    },
    {
      id: 3,
      name: 'Business College',
      courses: ['Business Administration', 'Economics', 'Accounting']
    }
  ];

  const mockJobPostings = [
    {
      id: 1,
      title: 'Junior Software Developer',
      company: 'Tech Solutions Inc.',
      location: 'New York',
      type: 'Full-time',
      requirements: ['Computer Science degree', 'JavaScript knowledge'],
      postedDate: '2024-01-15'
    },
    {
      id: 2,
      title: 'Data Analyst',
      company: 'Analytics Pro',
      location: 'Remote',
      type: 'Contract',
      requirements: ['Statistics background', 'SQL skills'],
      postedDate: '2024-01-16'
    }
  ];

  // Use useEffect for initial data loading
  useEffect(() => {
    if (isLoggedIn) {
      loadStudentData();
    }
  }, [isLoggedIn]);

  const loadStudentData = () => {
    setJobPostings(mockJobPostings);
    setNotifications([
      { id: 1, message: 'New job posting: Junior Software Developer', type: 'job', read: false },
      { id: 2, message: 'Application status updated for Computer Science', type: 'application', read: false }
    ]);
  };

  const handleRegister = (e) => {
    e.preventDefault();
    if (registerData.password !== registerData.confirmPassword) {
      alert('Passwords do not match!');
      return;
    }
    setIsRegistered(true);
    setStudentProfile(prev => ({
      ...prev,
      name: registerData.name,
      email: registerData.email
    }));
    alert('Registration successful! Please check your email for verification.');
  };

  const verifyEmail = () => {
    setEmailVerified(true);
    alert('Email verified successfully! You can now login.');
  };

  const handleLogin = (e) => {
    e.preventDefault();
    if (emailVerified) {
      setIsLoggedIn(true);
    } else {
      alert('Please verify your email first!');
    }
  };

  const handleCourseApplication = (e) => {
    e.preventDefault();

    const existingApplications = applications.filter(app => 
      app.institution === courseApplication.institution
    );

    if (existingApplications.length >= 2) {
      alert('You can only apply to maximum 2 courses per institution!');
      return;
    }

    const newApplications = [];

    if (courseApplication.course1) {
      newApplications.push({
        id: Date.now(),
        institution: courseApplication.institution,
        course: courseApplication.course1,
        status: 'pending',
        appliedDate: new Date().toLocaleDateString()
      });
    }

    if (courseApplication.course2 && courseApplication.course2 !== courseApplication.course1) {
      newApplications.push({
        id: Date.now() + 1,
        institution: courseApplication.institution,
        course: courseApplication.course2,
        status: 'pending',
        appliedDate: new Date().toLocaleDateString()
      });
    }

    if (newApplications.length > 0) {
      setApplications(prev => [...prev, ...newApplications]);
      setCourseApplication({ institution: '', course1: '', course2: '' });
      alert('Application submitted successfully!');

      setTimeout(() => {
        const results = newApplications.map(app => ({
          ...app,
          status: Math.random() > 0.5 ? 'admitted' : 'rejected',
          decisionDate: new Date().toLocaleDateString()
        }));
        setAdmissionsResults(prev => [...prev, ...results]);
      }, 3000);
    }
  };

  const handleDocumentUpload = (type, file) => {
    if (!file) return;

    const newDocument = {
      id: Date.now(),
      name: file.name,
      type: type,
      uploadDate: new Date().toLocaleDateString(),
      size: file.size
    };

    if (type === 'transcript') {
      setDocuments(prev => ({
        ...prev,
        transcripts: [...prev.transcripts, newDocument]
      }));
    } else {
      setDocuments(prev => ({
        ...prev,
        certificates: [...prev.certificates, newDocument]
      }));
    }
    alert(`${type.charAt(0).toUpperCase() + type.slice(1)} uploaded successfully!`);
  };

  const applyForJob = (jobId) => {
    const job = jobPostings.find(j => j.id === jobId);
    if (job && !appliedJobs.includes(jobId)) {
      setAppliedJobs(prev => [...prev, jobId]);
      alert(`Successfully applied for ${job.title} at ${job.company}`);
    }
  };

  const updateProfile = (e) => {
    e.preventDefault();
    alert('Profile updated successfully!');
  };

  const markNotificationAsRead = (notificationId) => {
    setNotifications(prev => 
      prev.map(notif => 
        notif.id === notificationId ? { ...notif, read: true } : notif
      )
    );
  };

  const handleLogout = () => {
    setIsLoggedIn(false);
    setEmailVerified(false);
    setIsRegistered(false);
    setApplications([]);
    setAdmissionsResults([]);
    setDocuments({ transcripts: [], certificates: [] });
    setAppliedJobs([]);
    setNotifications([]);
  };

  const getAvailableCourses = () => {
    const institution = availableInstitutions.find(inst => inst.name === courseApplication.institution);
    return institution ? institution.courses : [];
  };

  if (!isLoggedIn) {
    return (
      <div className="student-module">
        <h3>Student Module</h3>

        {!isRegistered ? (
          <div className="registration-section">
            <h4>Student Registration</h4>
            <form onSubmit={handleRegister}>
              <div className="form-group">
                <input
                  type="text"
                  placeholder="Full Name"
                  value={registerData.name}
                  onChange={(e) => setRegisterData(prev => ({...prev, name: e.target.value}))}
                  required
                />
              </div>
              <div className="form-group">
                <input
                  type="email"
                  placeholder="Email Address"
                  value={registerData.email}
                  onChange={(e) => setRegisterData(prev => ({...prev, email: e.target.value}))}
                  required
                />
              </div>
              <div className="form-group">
                <input
                  type="password"
                  placeholder="Password"
                  value={registerData.password}
                  onChange={(e) => setRegisterData(prev => ({...prev, password: e.target.value}))}
                  required
                />
              </div>
              <div className="form-group">
                <input
                  type="password"
                  placeholder="Confirm Password"
                  value={registerData.confirmPassword}
                  onChange={(e) => setRegisterData(prev => ({...prev, confirmPassword: e.target.value}))}
                  required
                />
              </div>
              <button type="submit">Register</button>
            </form>
            <p>Already registered? <button onClick={() => setIsRegistered(true)}>Login here</button></p>
          </div>
        ) : !emailVerified ? (
          <div className="verification-section">
            <h4>Email Verification Required</h4>
            <p>Please check your email for the verification link.</p>
            <button onClick={verifyEmail}>Verify Email</button>
            <p>Didn't receive the email? <button onClick={() => alert('Verification email sent again!')}>Resend</button></p>
          </div>
        ) : (
          <div className="login-section">
            <h4>Student Login</h4>
            <form onSubmit={handleLogin}>
              <div className="form-group">
                <input
                  type="email"
                  placeholder="Email"
                  value={loginData.email}
                  onChange={(e) => setLoginData(prev => ({...prev, email: e.target.value}))}
                  required
                />
              </div>
              <div className="form-group">
                <input
                  type="password"
                  placeholder="Password"
                  value={loginData.password}
                  onChange={(e) => setLoginData(prev => ({...prev, password: e.target.value}))}
                  required
                />
              </div>
              <button type="submit">Login</button>
            </form>
          </div>
        )}
      </div>
    );
  }

  return (
    <div className="student-module">
      <div className="student-header">
        <h3>Student Dashboard - Welcome, {studentProfile.name}!</h3>
        <div className="header-actions">
          <div className="notifications">
            <button className="notification-btn">
              Notifications ({notifications.filter(n => !n.read).length})
            </button>
            <div className="notification-dropdown">
              {notifications.map(notification => (
                <div 
                  key={notification.id} 
                  className={`notification-item ${notification.read ? 'read' : 'unread'}`}
                  onClick={() => markNotificationAsRead(notification.id)}
                >
                  {notification.message}
                </div>
              ))}
            </div>
          </div>
          <button onClick={handleLogout} className="logout-btn">Logout</button>
        </div>
      </div>

      <div className="profile-section">
        <h4>Student Profile</h4>
        <form onSubmit={updateProfile}>
          <div className="form-row">
            <input
              type="text"
              placeholder="Full Name"
              value={studentProfile.name}
              onChange={(e) => setStudentProfile(prev => ({...prev, name: e.target.value}))}
            />
            <input
              type="email"
              placeholder="Email"
              value={studentProfile.email}
              onChange={(e) => setStudentProfile(prev => ({...prev, email: e.target.value}))}
            />
          </div>
          <div className="form-row">
            <input
              type="text"
              placeholder="Phone Number"
              value={studentProfile.phone}
              onChange={(e) => setStudentProfile(prev => ({...prev, phone: e.target.value}))}
            />
            <input
              type="text"
              placeholder="Address"
              value={studentProfile.address}
              onChange={(e) => setStudentProfile(prev => ({...prev, address: e.target.value}))}
            />
          </div>
          <div className="form-group">
            <textarea
              placeholder="Educational Background"
              value={studentProfile.education}
              onChange={(e) => setStudentProfile(prev => ({...prev, education: e.target.value}))}
            />
          </div>
          <button type="submit">Update Profile</button>
        </form>
      </div>

      <div className="applications-section">
        <h4>Apply for Courses</h4>
        <form onSubmit={handleCourseApplication} className="application-form">
          <div className="form-group">
            <select
              value={courseApplication.institution}
              onChange={(e) => setCourseApplication(prev => ({...prev, institution: e.target.value, course1: '', course2: ''}))}
              required
            >
              <option value="">Select Institution</option>
              {availableInstitutions.map(inst => (
                <option key={inst.id} value={inst.name}>{inst.name}</option>
              ))}
            </select>
          </div>

          {courseApplication.institution && (
            <>
              <div className="form-group">
                <select
                  value={courseApplication.course1}
                  onChange={(e) => setCourseApplication(prev => ({...prev, course1: e.target.value}))}
                  required
                >
                  <option value="">Select First Course Preference</option>
                  {getAvailableCourses().map((course, index) => (
                    <option key={index} value={course}>{course}</option>
                  ))}
                </select>
              </div>

              <div className="form-group">
                <select
                  value={courseApplication.course2}
                  onChange={(e) => setCourseApplication(prev => ({...prev, course2: e.target.value}))}
                >
                  <option value="">Select Second Course Preference (Optional)</option>
                  {getAvailableCourses().map((course, index) => (
                    <option key={index} value={course}>{course}</option>
                  ))}
                </select>
              </div>
            </>
          )}

          <button type="submit">Apply for Courses</button>
        </form>

        <div className="current-applications">
          <h5>Your Applications</h5>
          {applications.length === 0 ? (
            <p>No applications submitted yet.</p>
          ) : (
            applications.map(application => (
              <div key={application.id} className="application-item">
                <strong>{application.course}</strong> at {application.institution}
                <span className={`status ${application.status}`}>
                  Status: {application.status}
                </span>
                <span>Applied: {application.appliedDate}</span>
              </div>
            ))
          )}
        </div>
      </div>

      <div className="admissions-results">
        <h4>Admissions Results</h4>
        <div className="results-list">
          {admissionsResults.length === 0 ? (
            <p>No admission results yet.</p>
          ) : (
            admissionsResults.map(result => (
              <div key={result.id} className={`result-item ${result.status}`}>
                <div className="result-info">
                  <strong>{result.course}</strong> at {result.institution}
                  <span className={`decision ${result.status}`}>
                    {result.status.toUpperCase()}
                  </span>
                  <span>Decision Date: {result.decisionDate}</span>
                </div>
              </div>
            ))
          )}
        </div>
      </div>

      <div className="documents-section">
        <h4>Upload Documents</h4>

        <div className="upload-group">
          <h5>Academic Transcripts</h5>
          <input
            type="file"
            accept=".pdf,.doc,.docx"
            onChange={(e) => handleDocumentUpload('transcript', e.target.files[0])}
          />
          <div className="uploaded-files">
            {documents.transcripts.map(doc => (
              <div key={doc.id} className="document-item">
                 {doc.name} ({doc.uploadDate})
              </div>
            ))}
          </div>
        </div>

        <div className="upload-group">
          <h5>Additional Certificates</h5>
          <input
            type="file"
            accept=".pdf,.doc,.docx,.jpg,.png"
            onChange={(e) => handleDocumentUpload('certificate', e.target.files[0])}
          />
          <div className="uploaded-files">
            {documents.certificates.map(doc => (
              <div key={doc.id} className="document-item">
                 {doc.name} ({doc.uploadDate})
              </div>
            ))}
          </div>
        </div>
      </div>

      <div className="job-postings-section">
        <h4>Available Job Postings</h4>
        <div className="jobs-list">
          {jobPostings.map(job => (
            <div key={job.id} className="job-item">
              <div className="job-header">
                <strong>{job.title}</strong>
                <span className="company">{job.company}</span>
              </div>
              <div className="job-details">
                <span> {job.location}</span>
                <span> {job.type}</span>
                <span> {job.postedDate}</span>
              </div>
              <div className="job-requirements">
                <strong>Requirements:</strong>
                <ul>
                  {job.requirements.map((req, index) => (
                    <li key={index}>{req}</li>
                  ))}
                </ul>
              </div>
              <button 
                onClick={() => applyForJob(job.id)}
                disabled={appliedJobs.includes(job.id)}
                className={appliedJobs.includes(job.id) ? 'applied' : ''}
              >
                {appliedJobs.includes(job.id) ? 'Applied' : 'Apply Now'}
              </button>
            </div>
          ))}
        </div>
      </div>

      <div className="applied-jobs-section">
        <h4>Your Job Applications</h4>
        <div className="applied-jobs-list">
          {appliedJobs.length === 0 ? (
            <p>No job applications yet.</p>
          ) : (
            appliedJobs.map(jobId => {
              const job = jobPostings.find(j => j.id === jobId);
              return job ? (
                <div key={job.id} className="applied-job-item">
                  <strong>{job.title}</strong> at {job.company}
                  <span>Status: Under Review</span>
                </div>
              ) : null;
            })
          )}
        </div>
      </div>
    </div>
  );
};

export default Student;
