import React, { useState, useEffect } from "react";
import "./Admin.css"; // optional if you want to style separately

function Admin() {
  // --- STATE VARIABLES ---
  const [institutions, setInstitutions] = useState([]);
  const [faculties, setFaculties] = useState([]);
  const [courses, setCourses] = useState([]);
  const [companies, setCompanies] = useState([]);
  const [reports, setReports] = useState([]);
  const [selectedInstitution, setSelectedInstitution] = useState(null);
  const [newInstitution, setNewInstitution] = useState("");
  const [newFaculty, setNewFaculty] = useState("");
  const [newCourse, setNewCourse] = useState("");

  // --- SIMULATED FETCH CALLS ---
  useEffect(() => {
    // Replace with actual API calls (fetch from backend)
    setInstitutions([
      { id: 1, name: "National University of Lesotho" },
      { id: 2, name: "Limkokwing University" },
    ]);
    setCompanies([
      { id: 1, name: "Vodacom Lesotho", status: "Approved" },
      { id: 2, name: "Standard Lesotho Bank", status: "Pending" },
    ]);
    setReports([
      { id: 1, type: "Registered Students", count: 1023 },
      { id: 2, type: "Active Companies", count: 27 },
    ]);
  }, []);

  // --- HANDLERS ---
  const addInstitution = () => {
    if (!newInstitution.trim()) return;
    setInstitutions([...institutions, { id: Date.now(), name: newInstitution }]);
    setNewInstitution("");
  };

  const deleteInstitution = (id) => {
    setInstitutions(institutions.filter((i) => i.id !== id));
  };

  const addFaculty = () => {
    if (!selectedInstitution || !newFaculty.trim()) return;
    setFaculties([
      ...faculties,
      { id: Date.now(), institutionId: selectedInstitution, name: newFaculty },
    ]);
    setNewFaculty("");
  };

  const addCourse = () => {
    if (!selectedInstitution || !newCourse.trim()) return;
    setCourses([
      ...courses,
      { id: Date.now(), institutionId: selectedInstitution, name: newCourse },
    ]);
    setNewCourse("");
  };

  const toggleCompanyStatus = (id) => {
    setCompanies(
      companies.map((c) =>
        c.id === id
          ? { ...c, status: c.status === "Approved" ? "Suspended" : "Approved" }
          : c
      )
    );
  };

  // --- RENDER ---
  return (
    <div className="admin-page">
      <h2>Admin Module</h2>
      <p>Manage institutions, admissions, and companies</p>

      {/* --- INSTITUTIONS SECTION --- */}
      <section className="admin-section">
        <h3>Manage Higher Learning Institutions</h3>
        <input
          type="text"
          value={newInstitution}
          onChange={(e) => setNewInstitution(e.target.value)}
          placeholder="Enter institution name"
        />
        <button onClick={addInstitution}>Add Institution</button>
        <ul>
          {institutions.map((inst) => (
            <li key={inst.id}>
              {inst.name}{" "}
              <button onClick={() => deleteInstitution(inst.id)}>Delete</button>
              <button onClick={() => setSelectedInstitution(inst.id)}>
                Select
              </button>
            </li>
          ))}
        </ul>
      </section>

      {/* --- FACULTIES --- */}
      <section className="admin-section">
        <h3>Add Faculty (Under Selected Institution)</h3>
        <input
          type="text"
          value={newFaculty}
          onChange={(e) => setNewFaculty(e.target.value)}
          placeholder="Enter faculty name"
        />
        <button onClick={addFaculty}>Add Faculty</button>
        <ul>
          {faculties.map((f) => (
            <li key={f.id}>
              {f.name} (Institution #{f.institutionId})
            </li>
          ))}
        </ul>
      </section>

      {/* --- COURSES --- */}
      <section className="admin-section">
        <h3>Add Course (Under Selected Institution)</h3>
        <input
          type="text"
          value={newCourse}
          onChange={(e) => setNewCourse(e.target.value)}
          placeholder="Enter course name"
        />
        <button onClick={addCourse}>Add Course</button>
        <ul>
          {courses.map((c) => (
            <li key={c.id}>
              {c.name} (Institution #{c.institutionId})
            </li>
          ))}
        </ul>
      </section>

      {/* --- COMPANIES MANAGEMENT --- */}
      <section className="admin-section">
        <h3>Manage Registered Companies</h3>
        <table border="1" cellPadding="6" width="100%">
          <thead>
            <tr>
              <th>Company Name</th>
              <th>Status</th>
              <th>Action</th>
            </tr>
          </thead>
          <tbody>
            {companies.map((c) => (
              <tr key={c.id}>
                <td>{c.name}</td>
                <td>{c.status}</td>
                <td>
                  <button onClick={() => toggleCompanyStatus(c.id)}>
                    {c.status === "Approved" ? "Suspend" : "Approve"}
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </section>

      {/* --- REPORTS --- */}
      <section className="admin-section">
        <h3>System Reports</h3>
        <ul>
          {reports.map((r) => (
            <li key={r.id}>
              {r.type}: <strong>{r.count}</strong>
            </li>
          ))}
        </ul>
      </section>

      {/* --- ADMISSIONS --- */}
      <section className="admin-section">
        <h3>Publish Admissions</h3>
        <button>Publish Admissions (Mock)</button>
        <p>Admissions publishing functionality to be integrated with backend API.</p>
      </section>
    </div>
  );
}

export default Admin;
