import React, { useState } from 'react';
import './Admin.css';

function Admin() {
  const [institutions, setInstitutions] = useState([]);
  const [admissions, setAdmissions] = useState([]);
  const [companies, setCompanies] = useState([]);
  const [reports, setReports] = useState([]);
  const [selectedInstitution, setSelectedInstitution] = useState(null);
  const [newInstitution, setNewInstitution] = useState({ name: '', location: '' });
  const [newFaculty, setNewFaculty] = useState({ name: '' });
  const [newCourse, setNewCourse] = useState({ name: '', code: '' });

  // Institution Management
  const addInstitution = () => {
    if (newInstitution.name && newInstitution.location) {
      const institution = {
        id: Date.now(),
        ...newInstitution,
        faculties: [],
        isPublished: false
      };
      setInstitutions([...institutions, institution]);
      setNewInstitution({ name: '', location: '' });
    }
  };

  const updateInstitution = (id, updates) => {
    setInstitutions(institutions.map(inst => 
      inst.id === id ? { ...inst, ...updates } : inst
    ));
  };

  const deleteInstitution = (id) => {
    setInstitutions(institutions.filter(inst => inst.id !== id));
  };

  // Faculty Management
  const addFaculty = (institutionId) => {
    if (newFaculty.name) {
      setInstitutions(institutions.map(inst => 
        inst.id === institutionId 
          ? { ...inst, faculties: [...inst.faculties, { id: Date.now(), ...newFaculty, courses: [] }] }
          : inst
      ));
      setNewFaculty({ name: '' });
    }
  };

  const deleteFaculty = (institutionId, facultyId) => {
    setInstitutions(institutions.map(inst => 
      inst.id === institutionId 
        ? { ...inst, faculties: inst.faculties.filter(fac => fac.id !== facultyId) }
        : inst
    ));
  };

  // Course Management
  const addCourse = (institutionId, facultyId) => {
    if (newCourse.name && newCourse.code) {
      setInstitutions(institutions.map(inst => 
        inst.id === institutionId 
          ? { 
              ...inst, 
              faculties: inst.faculties.map(fac => 
                fac.id === facultyId 
                  ? { ...fac, courses: [...fac.courses, { id: Date.now(), ...newCourse }] }
                  : fac
              )
            }
          : inst
      ));
      setNewCourse({ name: '', code: '' });
    }
  };

  const deleteCourse = (institutionId, facultyId, courseId) => {
    setInstitutions(institutions.map(inst => 
      inst.id === institutionId 
        ? { 
            ...inst, 
            faculties: inst.faculties.map(fac => 
              fac.id === facultyId 
                ? { ...fac, courses: fac.courses.filter(course => course.id !== courseId) }
                : fac
            )
          }
        : inst
    ));
  };

  // Admissions Management
  const publishAdmission = (institutionId) => {
    setInstitutions(institutions.map(inst => 
      inst.id === institutionId ? { ...inst, isPublished: true } : inst
    ));
    setAdmissions([...admissions, { 
      id: Date.now(), 
      institutionId, 
      publishedAt: new Date().toLocaleDateString() 
    }]);
  };

  const closeAdmission = (institutionId) => {
    setInstitutions(institutions.map(inst => 
      inst.id === institutionId ? { ...inst, isPublished: false } : inst
    ));
    setAdmissions(admissions.filter(adm => adm.institutionId !== institutionId));
  };

  // Company Management
  const approveCompany = (companyId) => {
    setCompanies(companies.map(comp => 
      comp.id === companyId ? { ...comp, status: 'approved' } : comp
    ));
  };

  const suspendCompany = (companyId) => {
    setCompanies(companies.map(comp => 
      comp.id === companyId ? { ...comp, status: 'suspended' } : comp
    ));
  };

  const deleteCompany = (companyId) => {
    setCompanies(companies.filter(comp => comp.id !== companyId));
  };

  return (
    <div className="admin-module">
      <h3>Admin Module</h3>

      {/* Login Section */}
      <div className="login-section">
        <h4>Admin Login</h4>
        <div className="login-form">
          <input type="text" placeholder="Username" />
          <input type="password" placeholder="Password" />
          <button>Login</button>
        </div>
      </div>

      {/* Institution Management */}
      <div className="institution-management">
        <h4>Manage Higher Learning Institutions</h4>

        {/* Add New Institution */}
        <div className="add-institution">
          <h5>Add New Institution</h5>
          <input 
            type="text" 
            placeholder="Institution Name" 
            value={newInstitution.name}
            onChange={(e) => setNewInstitution({...newInstitution, name: e.target.value})}
          />
          <input 
            type="text" 
            placeholder="Location" 
            value={newInstitution.location}
            onChange={(e) => setNewInstitution({...newInstitution, location: e.target.value})}
          />
          <button onClick={addInstitution}>Add Institution</button>
        </div>

        {/* Institutions List */}
        <div className="institutions-list">
          <h5>Institutions</h5>
          {institutions.map(institution => (
            <div key={institution.id} className="institution-item">
              <div className="institution-header">
                <strong>{institution.name}</strong> - {institution.location}
                <div className="institution-actions">
                  <button onClick={() => updateInstitution(institution.id, { name: prompt('New name:', institution.name) })}>
                    Update
                  </button>
                  <button onClick={() => deleteInstitution(institution.id)}>Delete</button>
                  <button onClick={() => setSelectedInstitution(selectedInstitution?.id === institution.id ? null : institution)}>
                    {selectedInstitution?.id === institution.id ? 'Collapse' : 'Expand'}
                  </button>
                </div>
              </div>

              {/* Faculty Management for Selected Institution */}
              {selectedInstitution?.id === institution.id && (
                <div className="faculty-management">
                  <h6>Manage Faculties</h6>

                  {/* Add Faculty */}
                  <div className="add-faculty">
                    <input 
                      type="text" 
                      placeholder="Faculty Name" 
                      value={newFaculty.name}
                      onChange={(e) => setNewFaculty({...newFaculty, name: e.target.value})}
                    />
                    <button onClick={() => addFaculty(institution.id)}>Add Faculty</button>
                  </div>

                  {/* Faculties List */}
                  {institution.faculties.map(faculty => (
                    <div key={faculty.id} className="faculty-item">
                      <div className="faculty-header">
                        <strong>{faculty.name}</strong>
                        <button onClick={() => deleteFaculty(institution.id, faculty.id)}>Delete</button>
                      </div>

                      {/* Course Management */}
                      <div className="course-management">
                        <h7>Courses</h7>

                        {/* Add Course */}
                        <div className="add-course">
                          <input 
                            type="text" 
                            placeholder="Course Name" 
                            value={newCourse.name}
                            onChange={(e) => setNewCourse({...newCourse, name: e.target.value})}
                          />
                          <input 
                            type="text" 
                            placeholder="Course Code" 
                            value={newCourse.code}
                            onChange={(e) => setNewCourse({...newCourse, code: e.target.value})}
                          />
                          <button onClick={() => addCourse(institution.id, faculty.id)}>Add Course</button>
                        </div>

                        {/* Courses List */}
                        {faculty.courses.map(course => (
                          <div key={course.id} className="course-item">
                            {course.code} - {course.name}
                            <button onClick={() => deleteCourse(institution.id, faculty.id, course.id)}>
                              Delete
                            </button>
                          </div>
                        ))}
                      </div>
                    </div>
                  ))}
                </div>
              )}

              {/* Admission Control */}
              <div className="admission-control">
                {institution.isPublished ? (
                  <button onClick={() => closeAdmission(institution.id)}>Close Admissions</button>
                ) : (
                  <button onClick={() => publishAdmission(institution.id)}>Publish Admissions</button>
                )}
                <span>Status: {institution.isPublished ? 'Published' : 'Closed'}</span>
              </div>
            </div>
          ))}
        </div>
      </div>

      {/* Company Management */}
      <div className="company-management">
        <h4>Manage Registered Companies</h4>
        <div className="companies-list">
          {companies.map(company => (
            <div key={company.id} className="company-item">
              <strong>{company.name}</strong> - Status: {company.status}
              <div className="company-actions">
                <button onClick={() => approveCompany(company.id)}>Approve</button>
                <button onClick={() => suspendCompany(company.id)}>Suspend</button>
                <button onClick={() => deleteCompany(company.id)}>Delete</button>
              </div>
            </div>
          ))}
        </div>
      </div>

      {/* System Reports */}
      <div className="system-reports">
        <h4>System Reports</h4>
        <div className="reports-list">
          {reports.map(report => (
            <div key={report.id} className="report-item">
              <strong>{report.title}</strong> - {report.date}
              <button>View Details</button>
            </div>
          ))}
        </div>
      </div>

      {/* Published Admissions Monitor */}
      <div className="admissions-monitor">
        <h4>Published Admissions</h4>
        <div className="admissions-list">
          {admissions.map(admission => (
            <div key={admission.id} className="admission-item">
              Institution ID: {admission.institutionId} - Published: {admission.publishedAt}
              <button>View Registered Users</button>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default Admin;
