import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './Admin.css';

const Admin = () => {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [activeTab, setActiveTab] = useState('dashboard');
  const [institutions, setInstitutions] = useState([]);
  const [admissions, setAdmissions] = useState([]);
  const [companies, setCompanies] = useState([]);
  const [reports, setReports] = useState([]);
  const [selectedInstitution, setSelectedInstitution] = useState(null);
  const [newInstitution, setNewInstitution] = useState({ name: '', location: '', type: '' });
  const [newFaculty, setNewFaculty] = useState({ name: '', dean: '' });
  const [newCourse, setNewCourse] = useState({ name: '', code: '', duration: '' });
  const [loginData, setLoginData] = useState({ email: '', password: '' });
  const [loginError, setLoginError] = useState('');
  const [isLoading, setIsLoading] = useState(false);

  const navigate = useNavigate();

  // Mock data for demonstration
  useEffect(() => {
    if (isLoggedIn) {
      // Load sample data
      setCompanies([
        { id: 1, name: 'Tech Solutions Inc.', status: 'pending', registrationDate: '2024-01-15' },
        { id: 2, name: 'Innovation Labs', status: 'approved', registrationDate: '2024-01-10' },
        { id: 3, name: 'Global Enterprises', status: 'suspended', registrationDate: '2024-01-08' }
      ]);

      setReports([
        { id: 1, title: 'Monthly User Activity', date: '2024-01-20', type: 'analytics' },
        { id: 2, title: 'System Performance', date: '2024-01-19', type: 'system' },
        { id: 3, title: 'Admission Statistics', date: '2024-01-18', type: 'admissions' }
      ]);
    }
  }, [isLoggedIn]);

  const handleLogin = async (e) => {
    e.preventDefault();
    setLoginError('');
    setIsLoading(true);

    // Basic validation
    if (!loginData.email.trim() || !loginData.password.trim()) {
      setLoginError('Please enter both email and password');
      setIsLoading(false);
      return;
    }

    // Email validation
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    if (!emailRegex.test(loginData.email)) {
      setLoginError('Please enter a valid email address');
      setIsLoading(false);
      return;
    }

    try {
      // Simulate API call - in real app, this would be your backend
      const isValid = await authenticateAdmin(loginData.email, loginData.password);

      if (isValid) {
        setIsLoggedIn(true);
        setActiveTab('dashboard');
        // Store login state
        localStorage.setItem('adminLoggedIn', 'true');
        localStorage.setItem('adminEmail', loginData.email);
      } else {
        setLoginError('Invalid email or password. Please try again.');
      }
    } catch (error) {
      setLoginError('Login failed. Please try again.');
    } finally {
      setIsLoading(false);
    }
  };

  // Simulated authentication - Replace with actual backend API call
  const authenticateAdmin = async (email, password) => {
    // Simulate API delay
    await new Promise(resolve => setTimeout(resolve, 1000));

    // In a real application, this would be an API call to your backend
    // For demo purposes, we'll accept any valid email format with password 'admin123'
    const validAdmins = [
      { email: 'admin@careerplatform.com', password: 'admin123', name: 'System Administrator' },
      { email: 'mpho@gmail.com', password: 'admin123', name: 'Mpho Admin' },
      { email: 'supervisor@careerplatform.com', password: 'super123', name: 'Platform Supervisor' }
    ];

    const admin = validAdmins.find(admin => 
      admin.email === email && admin.password === password
    );

    if (admin) {
      // Store admin info
      localStorage.setItem('adminName', admin.name);
      return true;
    }

    return false;
  };

  // Check if user is already logged in
  useEffect(() => {
    const loggedIn = localStorage.getItem('adminLoggedIn');
    const email = localStorage.getItem('adminEmail');

    if (loggedIn === 'true' && email) {
      setIsLoggedIn(true);
      setLoginData(prev => ({ ...prev, email }));
    }
  }, []);

  const handleLogout = () => {
    setIsLoggedIn(false);
    setLoginData({ email: '', password: '' });
    setLoginError('');
    // Clear all admin data from localStorage
    localStorage.removeItem('adminLoggedIn');
    localStorage.removeItem('adminEmail');
    localStorage.removeItem('adminName');
  };

  const goToHome = () => {
    navigate('/');
  };

  const getAdminName = () => {
    return localStorage.getItem('adminName') || loginData.email.split('@')[0];
  };

  // Institution Management
  const addInstitution = (e) => {
    e.preventDefault();
    if (newInstitution.name && newInstitution.location) {
      const institution = {
        id: Date.now(),
        ...newInstitution,
        faculties: [],
        isPublished: false,
        createdAt: new Date().toLocaleDateString(),
        createdBy: getAdminName()
      };
      setInstitutions([...institutions, institution]);
      setNewInstitution({ name: '', location: '', type: '' });
    }
  };

  const updateInstitution = (id, updates) => {
    setInstitutions(institutions.map(inst => 
      inst.id === id ? { ...inst, ...updates, updatedBy: getAdminName(), updatedAt: new Date().toLocaleDateString() } : inst
    ));
  };

  const deleteInstitution = (id) => {
    if (window.confirm('Are you sure you want to delete this institution?')) {
      setInstitutions(institutions.filter(inst => inst.id !== id));
    }
  };

  // Faculty Management
  const addFaculty = (institutionId, e) => {
    e.preventDefault();
    if (newFaculty.name) {
      setInstitutions(institutions.map(inst => 
        inst.id === institutionId 
          ? { 
              ...inst, 
              faculties: [...inst.faculties, { 
                id: Date.now(), 
                ...newFaculty, 
                courses: [],
                createdAt: new Date().toLocaleDateString(),
                createdBy: getAdminName()
              }] 
            }
          : inst
      ));
      setNewFaculty({ name: '', dean: '' });
    }
  };

  const deleteFaculty = (institutionId, facultyId) => {
    if (window.confirm('Are you sure you want to delete this faculty?')) {
      setInstitutions(institutions.map(inst => 
        inst.id === institutionId 
          ? { ...inst, faculties: inst.faculties.filter(fac => fac.id !== facultyId) }
          : inst
      ));
    }
  };

  // Course Management
  const addCourse = (institutionId, facultyId, e) => {
    e.preventDefault();
    if (newCourse.name && newCourse.code) {
      setInstitutions(institutions.map(inst => 
        inst.id === institutionId 
          ? { 
              ...inst, 
              faculties: inst.faculties.map(fac => 
                fac.id === facultyId 
                  ? { 
                      ...fac, 
                      courses: [...fac.courses, { 
                        id: Date.now(), 
                        ...newCourse,
                        createdAt: new Date().toLocaleDateString(),
                        createdBy: getAdminName()
                      }] 
                    }
                  : fac
              )
            }
          : inst
      ));
      setNewCourse({ name: '', code: '', duration: '' });
    }
  };

  const deleteCourse = (institutionId, facultyId, courseId) => {
    if (window.confirm('Are you sure you want to delete this course?')) {
      setInstitutions(institutions.map(inst => 
        inst.id === institutionId 
          ? { 
              ...inst, 
              faculties: inst.faculties.map(fac => 
                fac.id === facultyId 
                  ? { ...fac, courses: fac.courses.filter(course => course.id !== courseId) }
                  : fac
              )
            }
          : inst
      ));
    }
  };

  // Admissions Management
  const publishAdmission = (institutionId) => {
    setInstitutions(institutions.map(inst => 
      inst.id === institutionId ? { ...inst, isPublished: true } : inst
    ));
    const institution = institutions.find(inst => inst.id === institutionId);
    setAdmissions([...admissions, { 
      id: Date.now(), 
      institutionId, 
      institutionName: institution?.name,
      publishedAt: new Date().toLocaleDateString(),
      publishedBy: getAdminName(),
      status: 'active'
    }]);
  };

  const closeAdmission = (institutionId) => {
    setInstitutions(institutions.map(inst => 
      inst.id === institutionId ? { ...inst, isPublished: false } : inst
    ));
    setAdmissions(admissions.map(adm => 
      adm.institutionId === institutionId ? { ...adm, status: 'closed', closedBy: getAdminName() } : adm
    ));
  };

  // Company Management
  const approveCompany = (companyId) => {
    setCompanies(companies.map(comp => 
      comp.id === companyId ? { ...comp, status: 'approved', approvedBy: getAdminName(), approvedAt: new Date().toLocaleDateString() } : comp
    ));
  };

  const suspendCompany = (companyId) => {
    setCompanies(companies.map(comp => 
      comp.id === companyId ? { ...comp, status: 'suspended', suspendedBy: getAdminName(), suspendedAt: new Date().toLocaleDateString() } : comp
    ));
  };

  const deleteCompany = (companyId) => {
    if (window.confirm('Are you sure you want to delete this company?')) {
      setCompanies(companies.filter(comp => comp.id !== companyId));
    }
  };

  // Statistics
  const getStats = () => {
    return {
      totalInstitutions: institutions.length,
      activeAdmissions: admissions.filter(adm => adm.status === 'active').length,
      pendingCompanies: companies.filter(comp => comp.status === 'pending').length,
      totalReports: reports.length
    };
  };

  const stats = getStats();

  if (!isLoggedIn) {
    return (
      <div className="admin-module">
        <div className="admin-login-container">
          <div className="login-card">
            <div className="login-header">
              <h2>Admin Portal</h2>
              <p>System Administration Dashboard</p>
            </div>

            {loginError && (
              <div className="error-message">
                <strong>Login Failed:</strong> {loginError}
              </div>
            )}

            <form onSubmit={handleLogin} className="login-form">
              <div className="form-group">
                <label>Email Address *</label>
                <input
                  type="email"
                  placeholder="Enter your admin email"
                  value={loginData.email}
                  onChange={(e) => setLoginData({...loginData, email: e.target.value})}
                  required
                  disabled={isLoading}
                />
              </div>
              <div className="form-group">
                <label>Password *</label>
                <input
                  type="password"
                  placeholder="Enter your password"
                  value={loginData.password}
                  onChange={(e) => setLoginData({...loginData, password: e.target.value})}
                  required
                  disabled={isLoading}
                />
              </div>
              <button 
                type="submit" 
                className="btn-primary"
                disabled={isLoading}
              >
                {isLoading ? 'Logging in...' : 'Login to Admin Panel'}
              </button>
            </form>

            <div className="login-note">
              <div className="demo-credentials">
                <p><strong>Demo Credentials:</strong></p>
                <p>Email: <code>mpho@gmail.com</code></p>
                <p>Password: <code>admin123</code></p>
              </div>
              <button onClick={goToHome} className="home-btn" disabled={isLoading}>
                ← Back to Home
              </button>
            </div>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="admin-module">
      {/* Header */}
      <header className="admin-header">
        <div className="header-content">
          <div className="header-info">
            <button onClick={goToHome} className="home-btn">
              ← Back to Home
            </button>
            <div>
              <h2>Admin Dashboard</h2>
              <p>Welcome, {getAdminName()} • System Administration</p>
            </div>
          </div>
          <div className="header-actions">
            <span className="user-info">Logged in as: <strong>{loginData.email}</strong></span>
            <button onClick={handleLogout} className="logout-btn">Logout</button>
          </div>
        </div>
      </header>

      {/* Rest of the Admin component remains the same */}
      {/* ... (previous dashboard and management code) ... */}

    </div>
  );
};

export default Admin;
