import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './Admin.css';

const Admin = () => {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [activeTab, setActiveTab] = useState('dashboard');
  const [institutions, setInstitutions] = useState([]);
  const [admissions, setAdmissions] = useState([]);
  const [companies, setCompanies] = useState([]);
  const [reports, setReports] = useState([]);
  const [selectedInstitution, setSelectedInstitution] = useState(null);
  const [newInstitution, setNewInstitution] = useState({ name: '', location: '', type: '' });
  const [newFaculty, setNewFaculty] = useState({ name: '', dean: '' });
  const [newCourse, setNewCourse] = useState({ name: '', code: '', duration: '' });
  const [loginData, setLoginData] = useState({ username: '', password: '' });
  const [loginError, setLoginError] = useState('');

  const navigate = useNavigate();

  // Mock data for demonstration
  useEffect(() => {
    if (isLoggedIn) {
      // Load sample data
      setCompanies([
        { id: 1, name: 'Tech Solutions Inc.', status: 'pending', registrationDate: '2024-01-15' },
        { id: 2, name: 'Innovation Labs', status: 'approved', registrationDate: '2024-01-10' },
        { id: 3, name: 'Global Enterprises', status: 'suspended', registrationDate: '2024-01-08' }
      ]);

      setReports([
        { id: 1, title: 'Monthly User Activity', date: '2024-01-20', type: 'analytics' },
        { id: 2, title: 'System Performance', date: '2024-01-19', type: 'system' },
        { id: 3, title: 'Admission Statistics', date: '2024-01-18', type: 'admissions' }
      ]);
    }
  }, [isLoggedIn]);

  const handleLogin = (e) => {
    e.preventDefault();
    setLoginError('');

    // Basic validation
    if (!loginData.username.trim() || !loginData.password.trim()) {
      setLoginError('Please enter both username and password');
      return;
    }

    // In a real application, this would be an API call to your backend
    // For demo purposes, we'll simulate authentication
    const isValidCredentials = validateCredentials(loginData.username, loginData.password);

    if (isValidCredentials) {
      setIsLoggedIn(true);
      setActiveTab('dashboard');
      // Store login state in localStorage for persistence
      localStorage.setItem('adminLoggedIn', 'true');
      localStorage.setItem('adminUsername', loginData.username);
    } else {
      setLoginError('Invalid username or password. Please try again.');
    }
  };

  // Simulated credential validation - Replace with actual authentication
  const validateCredentials = (username, password) => {
    // In a real application, this would check against your database
    // For security, never store credentials in frontend code in production

