            </div>

            <div className="reports-content">
              <div className="reports-grid">
                {reports.map(report => (
                  <div key={report.id} className="report-card">
                    <div className="report-header">
                      <div className="report-icon">
                        {report.type === 'analytics' && '📈'}
                        {report.type === 'system' && '⚙️'}
                        {report.type === 'admissions' && '🎓'}
                      </div>
                      <h5>{report.title}</h5>
                    </div>
                    <div className="report-info">
                      <span>Date: {report.date}</span>
                      <span>Type: {report.type}</span>
                    </div>
                    <div className="report-actions">
                      <button className="btn-primary">View Details</button>
                      <button className="btn-secondary">Download</button>
                    </div>
                  </div>
                ))}
              </div>

              <div className="report-actions-panel">
                <h4>Generate New Report</h4>
                <div className="action-buttons">
                  <button className="btn-primary">User Activity Report</button>
                  <button className="btn-primary">System Performance</button>
                  <button className="btn-primary">Admission Analytics</button>
                  <button className="btn-primary">Company Statistics</button>
                </div>
              </div>
            </div>
          </div>
        )}
      </main>
    </div>
  );
};

export default Admin;
